import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../../context/CartContext';
import { useAuth } from '../../context/AuthContext';
import { formatPrice } from '../../lib/productService';
import { createOrder } from '../../lib/orderService';
import './Checkout.css';

function Checkout() {
  const navigate = useNavigate();
  const { items, getCartTotal, getCartCount, clearCart } = useCart();
  const { user, isAuthenticated, addAddress } = useAuth();

  const [step, setStep] = useState(1);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [selectedDelivery, setSelectedDelivery] = useState('standard');
  const [newAddress, setNewAddress] = useState({
    name: '',
    street: '',
    city: '',
    state: '',
    zip: '',
    country: 'United States'
  });
  const [showAddAddress, setShowAddAddress] = useState(false);

  const total = getCartTotal();
  const count = getCartCount();
  const shippingCost = selectedDelivery === 'express' ? 9.99 : 0;
  const taxRate = 0.08;
  const tax = total * taxRate;
  const orderTotal = total + shippingCost + tax;

  const { whole, fraction } = formatPrice(orderTotal);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/signin?redirect=checkout');
      return;
    }
    if (items.length === 0) {
      navigate('/cart');
      return;
    }
    if (user?.addresses?.length > 0) {
      const defaultAddr = user.addresses.find(a => a.isDefault) || user.addresses[0];
      setSelectedAddress(defaultAddr.id);
    }
  }, [isAuthenticated, items.length, navigate, user]);

  const handleAddAddress = (e) => {
    e.preventDefault();
    const addr = addAddress(newAddress);
    setSelectedAddress(addr.id);
    setShowAddAddress(false);
    setNewAddress({
      name: '',
      street: '',
      city: '',
      state: '',
      zip: '',
      country: 'United States'
    });
  };

  const handlePlaceOrder = () => {
    const address = user.addresses.find(a => a.id === selectedAddress);
    
    const order = createOrder({
      userId: user.id,
      items: items.map(item => ({
        productId: item.id,
        title: item.title,
        price: item.price,
        quantity: item.quantity,
        image: item.images[0],
        selectedVariant: item.selectedVariant
      })),
      shippingAddress: address,
      deliveryOption: selectedDelivery,
      totals: {
        subtotal: total,
        shipping: shippingCost,
        tax: tax,
        total: orderTotal
      },
      payment: {
        method: 'btc',
        status: 'pending'
      }
    });

    // Navigate to payment page with order ID
    navigate(`/payment?orderId=${order.id}`);
  };

  const deliveryOptions = [
    {
      id: 'standard',
      name: 'FREE Standard Shipping',
      description: 'Delivery in 3-5 business days',
      price: 0
    },
    {
      id: 'express',
      name: 'Express Shipping',
      description: 'Delivery in 1-2 business days',
      price: 9.99
    }
  ];

  return (
    <div className="checkout container">
      <h1 className="checkout__title">Checkout</h1>

      <div className="checkout__content">
        <div className="checkout__main">
          {/* Step 1: Shipping Address */}
          <div className={`checkout__section ${step >= 1 ? 'active' : ''}`}>
            <div className="section__header">
              <span className="section__number">1</span>
              <h2>Shipping address</h2>
              {step > 1 && (
                <button 
                  className="section__change"
                  onClick={() => setStep(1)}
                >
                  Change
                </button>
              )}
            </div>

            {step === 1 && (
              <div className="section__content">
                {user?.addresses?.length > 0 && (
                  <div className="address-list">
                    {user.addresses.map(addr => (
                      <label key={addr.id} className="address-option">
                        <input
                          type="radio"
                          name="address"
                          checked={selectedAddress === addr.id}
                          onChange={() => setSelectedAddress(addr.id)}
                        />
                        <div className="address-details">
                          <strong>{addr.name}</strong>
                          <span>{addr.street}</span>
                          <span>{addr.city}, {addr.state} {addr.zip}</span>
                          <span>{addr.country}</span>
                        </div>
                      </label>
                    ))}
                  </div>
                )}

                {!showAddAddress ? (
                  <button 
                    className="add-address-btn"
                    onClick={() => setShowAddAddress(true)}
                  >
                    + Add a new address
                  </button>
                ) : (
                  <form className="address-form" onSubmit={handleAddAddress}>
                    <h3>Add a new address</h3>
                    <div className="form-group">
                      <label className="form-label">Full name</label>
                      <input
                        type="text"
                        className="form-input"
                        value={newAddress.name}
                        onChange={(e) => setNewAddress({ ...newAddress, name: e.target.value })}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label className="form-label">Street address</label>
                      <input
                        type="text"
                        className="form-input"
                        value={newAddress.street}
                        onChange={(e) => setNewAddress({ ...newAddress, street: e.target.value })}
                        required
                      />
                    </div>
                    <div className="form-row">
                      <div className="form-group">
                        <label className="form-label">City</label>
                        <input
                          type="text"
                          className="form-input"
                          value={newAddress.city}
                          onChange={(e) => setNewAddress({ ...newAddress, city: e.target.value })}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label className="form-label">State</label>
                        <input
                          type="text"
                          className="form-input"
                          value={newAddress.state}
                          onChange={(e) => setNewAddress({ ...newAddress, state: e.target.value })}
                          required
                        />
                      </div>
                      <div className="form-group">
                        <label className="form-label">ZIP Code</label>
                        <input
                          type="text"
                          className="form-input"
                          value={newAddress.zip}
                          onChange={(e) => setNewAddress({ ...newAddress, zip: e.target.value })}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="btn btn-amazon">
                        Add address
                      </button>
                      <button 
                        type="button" 
                        className="btn btn-secondary"
                        onClick={() => setShowAddAddress(false)}
                      >
                        Cancel
                      </button>
                    </div>
                  </form>
                )}

                <button 
                  className="btn btn-amazon section__continue"
                  onClick={() => setStep(2)}
                  disabled={!selectedAddress}
                >
                  Use this address
                </button>
              </div>
            )}

            {step > 1 && selectedAddress && (
              <div className="section__summary">
                {(() => {
                  const addr = user.addresses.find(a => a.id === selectedAddress);
                  return addr ? (
                    <p>{addr.name}, {addr.street}, {addr.city}, {addr.state} {addr.zip}</p>
                  ) : null;
                })()}
              </div>
            )}
          </div>

          {/* Step 2: Delivery Options */}
          <div className={`checkout__section ${step >= 2 ? 'active' : ''}`}>
            <div className="section__header">
              <span className="section__number">2</span>
              <h2>Delivery options</h2>
              {step > 2 && (
                <button 
                  className="section__change"
                  onClick={() => setStep(2)}
                >
                  Change
                </button>
              )}
            </div>

            {step === 2 && (
              <div className="section__content">
                <div className="delivery-options">
                  {deliveryOptions.map(option => (
                    <label key={option.id} className="delivery-option">
                      <input
                        type="radio"
                        name="delivery"
                        checked={selectedDelivery === option.id}
                        onChange={() => setSelectedDelivery(option.id)}
                      />
                      <div className="delivery-details">
                        <strong>
                          {option.name}
                          {option.price > 0 && ` - $${option.price.toFixed(2)}`}
                        </strong>
                        <span>{option.description}</span>
                      </div>
                    </label>
                  ))}
                </div>

                <button 
                  className="btn btn-amazon section__continue"
                  onClick={() => setStep(3)}
                >
                  Use this delivery option
                </button>
              </div>
            )}

            {step > 2 && (
              <div className="section__summary">
                <p>{deliveryOptions.find(o => o.id === selectedDelivery)?.name}</p>
              </div>
            )}
          </div>

          {/* Step 3: Payment Method */}
          <div className={`checkout__section ${step >= 3 ? 'active' : ''}`}>
            <div className="section__header">
              <span className="section__number">3</span>
              <h2>Payment method</h2>
            </div>

            {step === 3 && (
              <div className="section__content">
                <div className="payment-method">
                  <div className="btc-payment-option selected">
                    <div className="btc-icon-large">â‚¿</div>
                    <div className="btc-details">
                      <strong>Bitcoin (BTC)</strong>
                      <span>Pay securely with cryptocurrency</span>
                      <span className="btc-note">You'll be shown a wallet address on the next screen</span>
                    </div>
                  </div>
                </div>

                <button 
                  className="btn btn-amazon section__continue"
                  onClick={handlePlaceOrder}
                >
                  Continue to payment
                </button>
              </div>
            )}
          </div>
        </div>

        {/* Order Summary */}
        <aside className="checkout__sidebar">
          <div className="order-summary card">
            <button 
              className="btn btn-amazon summary__btn"
              onClick={handlePlaceOrder}
              disabled={step < 3}
            >
              Place your order
            </button>

            <p className="summary__terms">
              By placing your order, you agree to Amazon Clone's privacy notice and conditions of use.
            </p>

            <div className="summary__divider"></div>

            <h3>Order Summary</h3>

            <div className="summary__row">
              <span>Items ({count}):</span>
              <span>${total.toFixed(2)}</span>
            </div>

            <div className="summary__row">
              <span>Shipping & handling:</span>
              <span>{shippingCost === 0 ? 'FREE' : `$${shippingCost.toFixed(2)}`}</span>
            </div>

            <div className="summary__row">
              <span>Estimated tax:</span>
              <span>${tax.toFixed(2)}</span>
            </div>

            <div className="summary__divider"></div>

            <div className="summary__total">
              <span>Order total:</span>
              <span className="total-price">
                <span className="price-symbol">$</span>
                <span className="price-whole">{whole}</span>
                <span className="price-fraction">{fraction}</span>
              </span>
            </div>
          </div>

          {/* Items Preview */}
          <div className="items-preview card">
            <h4>Items in your cart</h4>
            <div className="items-list">
              {items.map((item, index) => (
                <div key={index} className="item-preview">
                  <img src={item.images[0]} alt={item.title} />
                  <span className="item-qty">{item.quantity}</span>
                </div>
              ))}
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
}

export default Checkout;
